            Randomizer globalRandomizer = new Randomizer();

            List<Task> tasks = new List<Task>();

            //for (double exp = 0.2; exp <= 2.0; exp += 0.1)
            {
                //double tmpExp = exp;
                double tmpExp = 1.0;
                Randomizer r = globalRandomizer.NextRandomizer();
                tasks.Add(Task.Factory.StartNew(() =>
                {
                    Console.WriteLine($"Generating graphs for Exp:{tmpExp} {DateTime.Now.ToShortTimeString()}");
                    for (int currExperiment = 0; currExperiment < 500; currExperiment++)
