        }

        static void TryToFindFiEpsilonForErGraphs()
        {
            Console.WriteLine("Now running TryToFindFiEpsilonForErGraphs... " + DateTime.Now.ToShortTimeString());
            StringBuilder results = new StringBuilder();
            object resultsLock = new object();
            Randomizer globalRandomizer = new Randomizer();

            List<Task> tasks = new List<Task>();

            for (double p = 0.05; p <= 1.0; p += 0.05)
            {
                for (int n = 10; n <= 100000; n *= 2)
                {
                    double tmpP = p;
                    int tmpN = n;
                    Randomizer r = new Randomizer(new Random(globalRandomizer.Next()));
                    tasks.Add(Task.Factory.StartNew(() =>
                    {
                        Console.WriteLine($"Generating graph for p:{tmpP}, n:{tmpN}, {DateTime.Now.ToShortTimeString()}");
                        List<List<double>> allVectors = new List<List<double>>();
                        for (int currExp = 0; currExp < 200; currExp++)
                        {
                            try
                            {
                                ErGraph graph = new ErGraph(tmpN, tmpP, r);
                                allVectors.Add(graph.FiVector.ToList());
                            }
                            catch (Exception ex)
                            {
                                string resultLine = $"p:{tmpP}\tn:{tmpN}\tEXCEPTION:{ex.Message}";
                                Console.WriteLine(resultLine);
                                lock (resultsLock)
                                {
                                    results.AppendLine(resultLine);
                                }
                            }
                        }
                        var AvgFi = allVectors.SelectMany(d => d).Average();
                        var AvgOfAvg = allVectors.Average(l => l.Average(d => d));
                        var StdDev = StdDevP(allVectors.SelectMany(d => d));
                        var AvgOfStdDev = allVectors.Average(l => StdDevP(l));
                        var StdDevOfStdDev = StdDevP(allVectors.Select(StdDevP));
                        lock (resultsLock)
                        {
                            results.AppendLine(
                                $"p:{tmpP}\tn:{tmpN}\tGlobal Avg:{AvgFi}\tAvg of Avg:{AvgOfAvg}\tGlobal stddv:{StdDev}\tAvg of Std{AvgOfStdDev}\tStd Dev of Std Dev:{StdDevOfStdDev}");
                        }
                        Console.WriteLine($"Finished graph for p:{tmpP}, n:{tmpN}, {DateTime.Now.ToShortTimeString()}");
                    }));
                }
            }
            tasks.ForEach(t => t.Wait());
            File.WriteAllText("results.txt", results.ToString());
        }

        // help methods to get StdDev, should test and move into GraphLibyn, or something
        static double StdDevP(IEnumerable<int> values) => StdDevP(values.Select(v => (double)v));
        static double StdDevP(IEnumerable<decimal> values) => StdDevP(values.Select(v => (double)v));

        static double StdDevP(IEnumerable<double> values)
        {
            var vals = values.ToList();
            var mu = vals.Average();
            return vals.Sum(v => Math.Pow(v - mu, 2)) / vals.Count();
        }



