        static void BreakUpChessArrays()
        {
            int LIMIT = 16350;

            Randomizer rand = new Randomizer();

            var allFileLines = File.ReadAllLines(@"ChessResults.txt");
            var degrees_orig = allFileLines[1].Split('\t');
            var fis_orig = allFileLines[3].Split('\t');

            StringBuilder results = new StringBuilder();

            for (int i = 0; i < 10; i++)
            {
                Console.WriteLine($"Starting sample {i} at {DateTime.Now.ToShortTimeString()}");

                results.AppendLine($"Sample {i}");

                var degrees = degrees_orig.ToList();
                degrees.RemoveAt(0);
                var fis = fis_orig.ToList();
                fis.RemoveAt(0);

                List<string> newDegrees = new List<string>();
                List<string> newFis = new List<string>();

                for (int j = 0; j < LIMIT; j++)
                {
                    if (j % 10000 == 0)
                        Console.WriteLine($"Up to iteration {j} at {DateTime.Now.ToShortTimeString()}");
                    int nextIndex = (int)(rand.NextDouble() * degrees.Count);
                    newDegrees.Add(degrees[nextIndex]);
                    newFis.Add(fis[nextIndex]);
                    degrees.RemoveAt(nextIndex);
                    fis.RemoveAt(nextIndex);
                }

                results.AppendLine(string.Join("\t", newDegrees));
                results.AppendLine(string.Join("\t", newFis));
            }

            File.WriteAllText("ChessSamples.txt", results.ToString());
        }

        static void ProcessChessData()
        {
            string FILE = @"D:\Data\ChessScrape_02\ChesscomData.txt";
            var fileLines =
                File.ReadAllLines(FILE).Where(l => !string.IsNullOrWhiteSpace(l)).Select(l => l.Split('\t')).ToList();
            HashSet<string> scrapedIds =
                new HashSet<string>(fileLines.Select(l => l[0]));
            Graph chessGraph = new Graph();

            foreach (var line in fileLines)
            {
                var user = line[0];
                line[1].Split(',')
                    .Where(s => scrapedIds.Contains(s) && s != user)
                    .ToList()
                    .ForEach(n => chessGraph.AddEdge(user, n));
            }

            var maxDeg = chessGraph.Nodes.Where(n => n.Degree > 10000);

            maxDeg.Select(n => n.Id + "\t" + n.Degree).ToList().ForEach(Console.WriteLine);

            StringBuilder results = new StringBuilder();

            results.AppendLine($"Chess results");
            results.AppendLine($"Degree Vector\t" +
                               string.Join("\t",
                                   chessGraph.Nodes.OrderByDescending(n => n.Degree)
                                       .Select(n => n.Degree)));
            double alpha, sigma;
            PyPowerLaw.TryPythonPowerLaw(chessGraph.DegreeVector.Select(val => val.ToString()),
                out alpha,
                out sigma);
            results.AppendLine($"Alpha\t{alpha}\tSigma\t{sigma}");
            results.AppendLine($"FI Vector\t" +
                               string.Join("\t",
