        static IEnumerable<int> getValsForAlpha(double alpha, int xmin = 10)
        {
            Randomizer rand = new Randomizer();
            List<int> vals = new List<int>();

            for (int i = 0; i <= 10000; i++)
            {
                vals.Add(
                    (int)(xmin * Math.Pow(1.0 - rand.NextDouble(), -1 / (alpha - 1)))
                );
            }


            return vals;
        }

        static void TestBaGraph()
        {
            BaGraph baGraph = new BaGraph(5, new Randomizer(), 500, 0.9);
            var groups = baGraph.Nodes.GroupBy(n => n.Degree).OrderBy(g => g.Key);
            foreach (var g in groups)
            {
                Console.WriteLine(g.Key + ": " + g.Count());
            }
            Console.WriteLine();

        }

